    <script>
        $("#gform_submit_button_14").click(function(){
            var name = $('input[name="tester_name"]').val();
            var email = $('input[name="tester_email"]').val();
            var input_5 = $('input[name="input_5"]:checked').val();//input_5
            console.log(input_5);
            if(name==''){
                alert('The name is required');
                return false;
            }
            if(email==''){
                alert('The email is required');
                return false;
            }
            if(input_5===undefined){
                alert('Please accept the ClaimGiftsNow.com Terms and Policy');
                return false;
            }
            $.ajax({
                type: 'post',
                url: '/addTester',
                data: {'_token':'{{csrf_token()}}','name':name,'email':email},
                dataType:'json',
                success: function(res) {
                    if(res.status==1){
                        $('#gform_wrapper_14').css('display','none');
                        $('#gform_confirmation_wrapper_14').css('display','block');
                    }else{
                        alert(res.msg);
                    }
                }
            });
            return false;
        })

    </script>
