    <script>
        $("#gform_submit_button_1").click(function(){
            var input_1 = $('input[name="input_1"]:checked').val();
            var input_2 = $('input[name="input_2"]:checked').val();
            var input_3 = $('textarea[name="input_3"]').val();
            var requestId = $(this).attr('data-requestId');
            if(input_1===undefined){
                alert('The first field is required');
                return false;
            }
            if(input_2===undefined){
                alert('The second field is required');
                return false;
            }

            $.ajax({
                type: 'post',
                url: '/step/step-1',
                data: {'input_1':input_1,'_token':'{{csrf_token()}}','requestId':requestId,'input_2':input_2,'input_3':input_3},
                dataType:'json',
                success: function(res) {
                    if(res.status==1){
                        window.open('/step/step-2?requestId='+requestId);
                    }else{
                        alert(res.msg);
                    }
                }
            });
            return false;
        })

    </script>

