        <script>
        $("#continue-with-your-request").click(function(){
            var value = $('.amz-free-gift-textarea-with-copy-button-0').val();
            var requestId = $(this).attr('data-requestId');
            console.log(value);
            if(value){
                $.ajax({
                    type: 'post',
                    url: '/step/step-3',
                    data: {'input_1':value,'_token':'{{csrf_token()}}','requestId':requestId},
                    dataType:'json',
                    success: function(res) {
                        if(res.status==1){
                            window.open('/step/step-4?requestId='+requestId);
                        }else{
                            alert('System error');
                        }
                    }
                });
            }else{
                alert('Please enter the review');
            }
            return false;
        })

    </script>

