        <script>
        $("#gform_submit_button_3").click(function(){
            var name = $('input[name="name"]').val();
            var addressLine1 = $('input[name="addressLine1"]').val();
            var addressLine2 = $('input[name="addressLine2"]').val();
            var city = $('textarea[name="city"]').val();
            var phone = $('input[name="phone"]').val();
            var email = $('input[name="email"]').val();
            var requestId = $(this).attr('data-requestId');
            if(name==''){
                alert('The name is required');
                return false;
            }
            if(addressLine1==''){
                alert('The addressLine1 is required');
                return false;
            }
            if(city==''){
                alert('The city is required');
                return false;
            }
            if(email==''){
                alert('The email is required');
                return false;
            }

            $.ajax({
                type: 'post',
                url: '/step/step-4',
                data: {'name':name,'_token':'{{csrf_token()}}','requestId':requestId,'addressLine1':addressLine1,'addressLine2':addressLine2,'city':city,'phone':phone,'email':email},
                dataType:'json',
                success: function(res) {
                    if(res.status==1){
                        window.open('/step/step-5');
                    }else{
                        alert(res.msg);
                    }
                }
            });
            return false;
        })

    </script>


